import React, { useState } from 'react';
import axios from 'axios';

export default function AddDeveloper() {
    const [nickname, setNickname] = useState('');
    const [name, setName] = useState('');
    const [birthDate, setBirthDate] = useState('');
    const [stack, setStack] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const developerData = {
            nickname,
            name,
            birth_date: birthDate,
            stack: stack.split(',').map(s => s.trim())  // converte a string em array, removendo espaços extras
        };

        axios.post('http://localhost:8000/api/devs', developerData)
            .then(response => {
                alert('Desenvolvedor adicionado com sucesso!');
                setNickname('');
                setName('');
                setBirthDate('');
                setStack('');
            })
            .catch(error => {
                alert('Erro ao adicionar desenvolvedor!');
                console.error('Erro:', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Adicionar Desenvolvedor</h1>
            <label>
                Nickname:
                <input type="text" value={nickname} onChange={(e) => setNickname(e.target.value)} required />
            </label>
            <br />
            <label>
                Nome:
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </label>
            <br />
            <label>
                Data de Nascimento:
                <input type="date" value={birthDate} onChange={(e) => setBirthDate(e.target.value)} required />
            </label>
            <br />
            <label>
                Pilha Tecnológica (separado por vírgula):
                <input type="text" value={stack} onChange={(e) => setStack(e.target.value)} />
            </label>
            <br />
            <button type="submit">Adicionar</button>
        </form>
    );
}
